        return y if x > y else x

    def Gcd(x, y):
        return Gcd(y % x, x) if x != 0 else y

    def Lcm(x, y):
        return x * y // Gcd(x, y)
    d = [0] * n
    ok = 0
    cur = 0
    length = Lcm(7, n)
    for i in range(7 * n):
        if ink_volumes[i % n] == 0:
            return i % n + 1
        if cur != 6:
            ink_volumes[i % n] -= 1
            d[i % n] += 1
        cur = (cur + 1) % 7
    if ok == 0:
        k = 10 ** 20
        for i in range(n):
            ink_volumes[i] += d[i]
            if d[i] == 0:
                continue
            if ink_volumes[i] % d[i] > 0:
                k = Min(k, ink_volumes[i] // d[i])
            else:
                k = Min(k, ink_volumes[i] // d[i] - 1)
        if k == 10 ** 20:
            k = 0
        for i in range(n):
            ink_volumes[i] -= k * d[i]
        iter = 0
        cur = 0
        while True:
            if ink_volumes[iter] == 0:
                return iter % n + 1
            else:
                if cur != 6:
                    ink_volumes[iter] -= 1
                cur = cur * 1 % 7
                iter = (iter + 1) % n

