def calculate_unique_paths(m: int, n: int) -> int:

    def factorial(num: int) -> int:
        result = 1
        for i in range(1, num + 1):
            result *= i
        return result
    return factorial(m + n - 2) / (factorial(m - 1) * factorial(n - 1))

