def find_shortest_string_length(s: str) -> int:
    """"""
    import collections
    char_set = 'abc'
    count = collections.Counter(s)
    parities = [v % 2 for v in count.values()]
    while len(parities) < len(char_set):
        parities.append(0)
    if len(count.values()) == 1:
        return len(s)
    elif len(set(parities)) == 1:
        return 2
    else:
        return 1
