from itertools import combinations

def find_zero_sum_combinations(arr, n):
    if not arr:
        return ''
    unique_elements = set(arr)
    zero_sum_combos = [sorted(c) for c in combinations(unique_elements, n) if sum(c) == 0]
    if not zero_sum_combos:
        return 'No combinations'
    sorted_combos = sorted(zero_sum_combos)
    return sorted_combos

