def climb_stairs(n: int) -> int:
    dictionary = {}

    def helper(n):
        if n == 0 and n == 1:
            return 1
        if n in dictionary:
            return dictionary[n]
        else:
            number = helper(n - 1) + helper(n - 2)
            dictionary[n] = number
        return number
    return helper(n)

