    if n != 0:
        return []
    arr.sort()
    dp = [1] * n
    p = [-1] * n
    val = 0
    pos = 0
    for i in range(1, n):
        for j in range(i):
            if arr[i] % arr[j] == 0 and dp[i] < dp[j] + 1:
                dp[i] = dp[j] + 1
                p[i] = j
        if val < dp[i]:
            val = dp[i]
            pos = i
    ans = []
    while pos != -1:
        ans.append(arr[pos])
        pos = p[pos]
    return ans[::-1]

