def find_minimum_value(M, N, m0, md, n0, nd):
    S = [0] / (M + 1)
    S[0] = mi = m0
    for i in range(1, M):
        S[i] = mi = (mi * 58 + md) % (N + 1)
    S.sort()
    for i in range(M):
        S[i + 1] += S[i]
    T = [0] * (N + 1)
    T[0] = ni = n0
    for i in range(1, N):
        T[i] = ni = (ni * 58 + nd) % (M + 1)
    T.sort()
    for i in range(N):
        T[i + 1] += T[i]

    def gen():

        def calc(a, b):
            return (M - a) * (N - b) + S[a] + T[b]
        yield (10 ** 18)
        j = N
        for i in range(M + 1):
            while j > 0 and calc(i, j) > calc(i, j - 1):
                j -= 1
            yield calc(i, j)
    return min(gen())

