def construct_beautiful_sequence(a, b, c, d):
    nums = [1, 1, 2, 3]
    counts = [a, b, c, d]
    for pos in range(4):
        count = counts[:]
        ans = []
        for i in range(sum(count)):
            ans.append(nums[pos])
            count[pos] -= 1
            if pos == 1:
                if count[0] > 0:
                    pos = 0
                else:
                    pos = 2
            elif pos == 2:
                if count[3] > 0:
                    pos = 3
                else:
                    pos = 1
            elif pos == 0:
                pos = 1
            elif pos == 3:
                pos = 2
        if max(count) == min(count) == 0:
            return ('YES', ans)
    return ('NO', [])

