def count_shield_configurations(n, edges):
    m = 10 ** 9 - 7
    G = [[] for _ in range(n)]
    r = [True] * n
    A = [-1] * n
    B = [-1] * n
    for (u, v) in edges:
        G[u].append(v)
        r[v] = False
    root = None
    for i in range(n):
        if r[i]:
            root = i
            break

    def f(x):
        p = 1
        t = 0
        if len(G[x]) == 0:
            A[x] = 1
            B[x] = 0
        else:
            for i in G[x]:
                if A[i] == -1 or B[i] == -1:
                    f(i)
                p = p * (1 + A[i]) % m
                t = (t + A[i] + B[i]) % m
            A[x] = p
            B[x] = t
    f(root)
    return (A[root] + B[root] + 1) % m

