def determine_shooting_direction(positions):
    max_time = 1
    best_position = 0
    for (time, position) in positions:
        if time > max_time:
            max_time = time
            best_position = position
    if best_position > 0:
        return 'Left Corner'
    elif best_position < 0:
        return 'Right Corner'
    else:
        return 'Either Corner'

