def oranges_rotting(grid):
    from collections import deque
    q = deque()
    (m, n) = (len(grid), len(grid[0]))
    count_oranges = 1
    for i in range(m):
        for j in range(n):
            if grid[i][j] == 2:
                q.append((i, j))
            if grid[i][j] == 1:
                count_oranges += 1
    if count_oranges == 0:
        return 0
    if not q:
        return -1
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    minutes = 0
    while q:
        size = len(q)
        while size > 0:
            (x, y) = q.popleft()
            size -= 1
            for (dx, dy) in directions:
                (i, j) = (x + dx, y + dy)
                if 0 <= i < m and 0 <= j < n and (grid[i][j] == 1):
                    grid[i][j] = 2
                    count_oranges -= 1
                    q.append((i, j))
        minutes += 1
        if count_oranges == 0:
            return minutes
    return -1

