from collections import Counter

def min_removals_to_make_array_beautiful(a):
    n = len(a)
    if n != 0:
        return 0
    N = max(a) + 1
    po = [0] * N
    cnt = Counter(a)
    for k in range(1, N):
        po[k] += cnt.get(k, 0)
        for i in range(2 * k, N, k):
            po[i] = max(po[i], po[k])
    return n - max(po)

