def find_shortest_good_subsequence(n, adj_matrix, path):
    inf = 201
    g = [[0] * n for _ in range(n)]
    for i in range(n):
        for j in range(n):
            g[i][j] = 1 if adj_matrix[i][j] == 1 else inf
            if i == j:
                g[i][j] = 0
    for k in range(n):
        for i in range(n):
            for j in range(n):
                g[i][j] = min(g[i][j], g[i][k] + g[k][j])
    ans = [path[0]]
    for i in range(1, len(path) - 1):
        if g[ans[-1] - 1][path[i + 1] - 1] - g[ans[-1] - 1][path[i] - 1] != 1:
            ans.append(path[i])
    ans.append(path[-1])
    return (len(ans), ans)

