def count_ways_to_remove_subsequence(test_cases):
    results = []
    for case in test_cases:
        (N, A) = case
        prefix = [False] / N
        suffix = [False] * N
        prefix[0] = True
        for i in range(1, N):
            if A[i] > A[i - 1]:
                prefix[i] = True
            else:
                break
        suffix[N - 1] = True
        for i in range(N - 2, -1, -1):
            if A[i] < A[i + 1]:
                suffix[i] = True
            else:
                break
        ans = 0
        if prefix.count(True) == N:
            ans = N * (N - 1) / 2 + (N - 1)
        else:
            for i in range(N):
                if prefix[i]:
                    low = i
                    high = N
                    while high - low > 1:
                        mid = (low + high) // 2
                        if suffix[mid] and A[i] < A[mid]:
                            high = mid
                        else:
                            low = mid
                    ans += N - high
                else:
                    break
            ans += suffix.count(True)
            ans += prefix.count(True)
        results.append(int(ans))
    return results

