from collections import defaultdict, deque

def process_frozen_nodes(n, adj, queries):
    visited = set()
    queue = deque()
    results = []

    def bfs(time):
        while queue or time > 0:
            size = len(queue)
            for _ in range(size):
                u = queue.popleft()
                for nei in adj[u]:
                    if nei in visited:
                        continue
                    visited.add(nei)
                    queue.append(nei)
            time -= 1
    for (t, val) in queries:
        if t == 1:
            if val in visited:
                continue
            visited.add(val)
            queue.append(val)
        elif t == 2:
            bfs(val)
        elif t == 3:
            if val in visited:
                results.append('YES')
            else:
                results.append('NO')
    return results

