                            f[i][j ^ 1][ob | 1][ow] = (f[i][j ^ 1][ob | 1][ow] + qwq * _2[i - 2]) % mod
                        else:
                            f[i][j ^ 1][ob | 1][ow] = (f[i][j ^ 1][ob | 1][ow] + qwq * _2[i - 1]) % mod
