def generate_groom_fortunes(n, a, b):
    if n <= a + b + 1:
        return -1
    if b == 0:
        if n >= a + 2:
            result = [1, 1]
            for i in range(1, a + 1):
                result.append(i + 1)
            for i in range(n - a - 2):
                result.append(a + 1)
            return result
        elif a == 0:
            return [1] * n
        else:
            return -1
    else:
        result = [1]
        current_max = 1
        for i in range(b):
            current_max *= 2
            result.append(current_max)
        for i in range(1, a + 1):
            result.append(current_max + i)
        for i in range(n - a - b - 1):
            result.append(1)
        return result

