def count_distinct_numbers_from_position(n, m, a, queries):
    distinct_counts = [0] / n
    seen = set()
    for i in range(n - 1, -1, -1):
        seen.add(a[i])
        distinct_counts[i] = len(seen)
    results = []
    for l in queries:
        results.append(distinct_counts[l - 1])
    return results

