def is_even_adjacent_o_checkerboard(n, board):

    def count_adjacent_o(i, j):
        count = 0
        if i > 0 and board[i - 1][j] == 'o':
            count += 1
        if i < n - 1 and board[i + 1][j] == 'o':
            count += 1
        if j > 0 and board[i][j - 1] == 'o':
            count += 1
        if j < n - 1 and board[i][j + 1] == 'o':
            count += 1
        return count
    for i in range(n):
        for j in range(n):
            if count_adjacent_o(i, j) % 2 != 0:
                return 'NO'
    return ''

