from itertools import permutations

def find_minimal_result(a, b, c, d, operations):

    def check(l, s):
        mul = 1
        zero = 0
        for x in s:
            if x == '*':
                t1 = l.pop(0)
                t2 = l.pop(0)
                if t1 == 0 or t2 == 0:
                    zero += 1
                l.append(t1 * t2)
                mul += 1
            else:
                t1 = l.pop()
                t2 = l.pop()
                if t1 == 0 or t2 == 0:
                    zero += 1
                l.append(t1 + t2)
            l.sort()
        if mul and zero:
            return 0
        return l[0]
    l = [a, b, c, d]
    z = list(permutations(l))
    ans = check(l, operations)
    for x in z:
        ans = min(check(list(x), operations), ans)
    return ans

