def find_max_divisible_by_2(N: int) -> int:
    max_count = 1
    result = 1
    for i in range(1, N + 1):
        count = 0
        num = i
        while num % 2 == 0:
            num //= 2
            count += 1
        if count > max_count:
            max_count = count
            result = i
    return result

