def find_balanced_pairs(points):
    n = len(points)
    d = {i - 1: points[i] for i in range(n)}
    pairs = []
    for i in range(1, n + 1):
        if d[i] != -1:
            ans = 1e+18
            k = [-1, -1]
            (x1, y1, z1) = d[i]
            for j in range(1, n + 1):
                if d[j] != -1 and i != j:
                    (x2, y2, z2) = d[j]
                    aa = abs(x1 - x2) + abs(y1 - y2) + abs(z1 - z2)
                    if aa < ans:
                        ans = aa
                        k[0] = i
                        k[1] = j
            pairs.append((k[0], k[1]))
            d[k[0]] = -1
            d[k[1]] = -1
    return pairs

