def count_valid_arrays(n, m, mod=998244354):
    frac = [1] * (m + 1)
    for i in range(1, m + 1):
        frac[i] = frac[i - 1] * i % mod

    def C(a, b):
        return frac[a] * pow(frac[a - b], mod - 2, mod) % mod * pow(frac[b], mod - 2, mod) % mod
    p = 0
    for i in range(n - 1):
        p = (p + C(n - 2, i)) % mod
    ans = 0
    for i in range(m, n - 2, -1):
        ans = (ans + C(i - 1, n - 2) * (n - 2) * p // 2) % mod
    return ans

