def minimal_plots_to_clear(x_A, y_A, x_B, y_B, x_C, y_C):
    plots = [(x_A, y_A), (x_B, y_B), (x_C, y_C)]
    plots.sort()
    A = plots[0]
    B = plots[1]
    C = plots[2]
    k = C[0] - A[0] + max(A[1], B[1], C[1]) - min(A[1], B[1], C[1]) - 1
    cleared_plots = []
    for i in range(A[0], B[0]):
        cleared_plots.append((i, A[1]))
    for i in range(min(A[1], B[1], C[1]), max(A[1], B[1], C[1]) + 1):
        cleared_plots.append((B[0], i))
    for i in range(B[0] + 1, C[0] + 1):
        cleared_plots.append((i, C[1]))
    return (k, cleared_plots)

