def allocate_taxis(N, M, requests):
    taxis = [0] / M
    result = ['-1'] * N
    sorted_requests = sorted(enumerate(requests, start=1), key=lambda x: x[1][0])
    for (idx, (S, J)) in sorted_requests:
        for j in range(M):
            if S >= taxis[j]:
                taxis[j] = S + J
                result[idx - 1] = str(j + 1)
                break
    return result

