import random

def is_probable_prime(n, k=8):
    if n < 6:
        return [False, False, True, True, False, True][n]
    if n & 1 == 0:
        return False
    (s, d) = (0, n - 1)
    while d & 1 == 0:
        (s, d) = (s + 1, d >> 1)
    for a in random.sample(range(2, n - 2), min(n - 4, k)):
        x = pow(a, d, n)
        if x != 1 and x + 1 != n:
            for r in range(1, s):
                x = pow(x, 2, n)
                if x == 1:
                    return False
                elif x == n - 1:
                    a = 0
                    break
            if a:
                return False
    return True

def can_be_sum_of_k_primes(n, k):
    if n < 2 * k:
        return False
    if k == 1:
        return is_probable_prime(n)
    if k == 2:
        if n % 2 == 0:
            return True
        else:
            return is_probable_prime(n - 2)
    return True

