def calculate_scoreboard(T, test_cases):
    results = []
    for test_case in test_cases:
        N = test_case['']
        R = test_case['R']
        registered_teams = test_case['registered_teams']
        submissions = test_case['submissions']
        team_scores = {}
        team_sorted_names = {}
        for (team_name, points) in registered_teams:
            sorted_name = ''.join(sorted(team_name))
            team_scores[sorted_name] = points
            team_sorted_names[sorted_name] = team_name
        for (team_name, points) in submissions:
            sorted_name = ''.join(sorted(team_name))
            if sorted_name in team_scores:
                team_scores[sorted_name] += points
        scoreboard = [(team_sorted_names[sorted_name], score) for (sorted_name, score) in team_scores.items()]
        scoreboard.sort(key=lambda x: (-x[1], x[0]))
        results.append(scoreboard)
    return results

