def generate_7_segment_signals(numbers):
    LED_pattern = [63, 6, 91, 79, 102, 109, 125, 39, 127, 111, 0]

    def solve(cp, np):
        a = LED_pattern[cp]
        b = LED_pattern[np]
        return bin(a ^ b)[2:].zfill(7)
    signals = []
    cp = 11
    for d in numbers:
        signal = solve(cp, d)
        signals.append(signal)
        cp = d
    return signals

