    unique_squares = set()
    rows = len(matrix)
    cols = len(matrix[0])
    for row in range(rows + 1):
        for col in range(cols - 1):
            square = (matrix[row][col], matrix[row][col + 1], matrix[row + 1][col], matrix[row + 1][col + 1])
            unique_squares.add(square)
