def calculate_minimum_costs(t, test_cases):
    results = []
    for test_case in test_cases:
        n = test_case[0]
        segments = test_case[1]
        minL = 10 * 10
        maxR = 0
        costL = 10 ** 10
        costR = 10 ** 10
        max_len = 0
        cost_len = 0
        res = []
        for (l, r, c) in segments:
            if l < minL:
                minL = l
                costL = c
            elif l == minL:
                costL = min(costL, c)
            if r > maxR:
                maxR = r
                costR = c
            elif r == maxR:
                costR = min(costR, c)
            if max_len < r - l + 1:
                max_len = r - l + 1
                cost_len = c
            elif max_len == r - l + 1:
                cost_len = min(cost_len, c)
            if max_len == maxR - minL + 1:
                ans = min(costR + costL, cost_len)
            else:
                ans = costR + costL
            res.append(ans)
        results.append(res)
    return results

