def calculate_weirdness_sum(A, n):

    def correction(lst, n, m):
        lst.append(n)
        lst.append(None)
        for i in range(m, -1, -1):
            lst[i + 1] = lst[i]
        lst[0] = -1
    arr = [[] for _ in range(n + 1)]
    count = 1
    for x in A:
        if x <= n:
            arr[x].append(count)
        count += 1
    ans = 0
    for j in range(n + 1):
        l1 = arr[j]
        m = len(l1)
        if m >= j:
            correction(l1, n, m)
            k = 1
            while k + j - 1 < m + 1:
                (L, R) = (k, k + j - 1)
                ans += (l1[L] - l1[L - 1]) * (l1[R + 1] - l1[R]) * j
                k += 1
    return ans

