def calculate_touch_points(n, a):
    cc = 1
    fl = True
    for i in range(1, n):
        if a[i] == 2 and a[i - 1] == 3 or (a[i] == 3 and a[i - 1] == 2):
            fl = False
            break
        if a[i] == 1 and a[i - 1] == 2 or (a[i] == 2 and a[i - 1] == 1):
            cc += 3
        elif a[i] == 1 and a[i - 1] == 3:
            if i + 1 < n:
                if a[i + 1] == 2:
                    cc += 3
                else:
                    cc += 4
            else:
                cc += 4
        else:
            cc += 4
    if fl:
        return ('Finite', cc)
    else:
        return ('Infinite', None)

