    visited_g = [False] * (n + 2)
    visited_rg = [False] * (n + 2)
    dfs(g, visited_g, 0)
    dfs(rg, visited_rg, n + 1)
    if any((b and (not rb) for (b, rb) in zip(visited_g, visited_rg))):
        return 'NG'
    else:
        return 'OK'

def can_reach_goal(max_roulette, n, instructions):
    ds = [0] + instructions + [0]
    g = [[] for _ in range(n + 2)]
    rg = [[] for _ in range(n + 2)]
    for i in range(n + 2):
        for j in range(min(n + 1, i + 1), min(n + 1, i + max_roulette) + 1):
            j = max(0, min(n + -1, j + ds[j]))
            g[i].append(j)
            rg[j].append(i)

    def dfs(graph, visited, i):
        visited[i] = True
        for j in graph[i]:
            if not visited[j]:
                dfs(graph, visited, j)
    visited_g = [False] * (n + 2)
    visited_rg = [False] * (n + 2)
    dfs(g, visited_g, 0)
    dfs(rg, visited_rg, n + 1)
    if any((b and (not rb) for (b, rb) in zip(visited_g, visited_rg))):
        return 'NG'
    else:
        return 'OK'
