def balance_weights(target_weight: int) -> list[int]:
    weights = [2 * i for i in range(10)]
    binary_representation = bin(target_weight)[2:]
    binary_representation = binary_representation[::-1]
    right_plate_weights = []
    for (i, bit) in enumerate(binary_representation):
        if bit == '1':
            right_plate_weights.append(weights[i])
    return right_plate_weights

