def is_prime(num):
    if num < 1:
        return False
    if num <= 3:
        return True
    if num % 2 == 0 or num % 3 == 0:
        return False
    i = 5
    while i * i <= num:
        if num % i == 0 or num % (i + 2) == 0:
            return False
        i += 6
    return True

def is_palindrome(num):
    str_num = str(num)
    return str_num == str_num[::-1]

def find_smallest_prime_palindrome(X):
    if X % 2 == 0:
        X += 1
    while True:
        if is_palindrome(X) and is_prime(X):
            return X
        X += 2

