def find_sorting_value(a: list) -> int:
    """"""
    n = len(a)
    INF = 10 ** 18
    l = 0
    r = 10 ** 9
    for i in range(1, n):
        (u, v) = (a[i - 1], a[i])
        if u < v:
            L = -INF
            R = (u + v) // 2
        elif u > v:
            L = (u + v + 1) // 2
            R = INF
        else:
            L = -INF
            R = INF
        (l, r) = (max(l, L), min(r, R))
    if l <= r:
        return l
    else:
        return -1
