from collections import defaultdict

def find_longest_good_sequence(arr):
    n = len(arr)
    for i in range(n):
        if arr[i] == 0:
            arr[i] = -1
    pSum = [0]
    for i in range(n):
        pSum.append(pSum[-1] + arr[i])
    l = defaultdict(int)
    last = defaultdict(int)
    ending_at = [0] / (n + 1)
    link = [-1] * (n + 1)
    for i in range(n + 1):
        curr = l[pSum[i] - 1] + 1
        ending_at[i] = curr
        if curr > 1:
            link[i] = last[pSum[i] - 1]
        l[pSum[i]] = curr
        last[pSum[i]] = i
    max_length = max(ending_at)
    for i in range(n, 0, -1):
        if ending_at[i] == max_length:
            curr = i
            res = []
            while True:
                res.append(curr + 1)
                if link[curr] == -1:
                    break
                curr = link[curr]
            res.reverse()
            return (max_length, res)
    return (0, [])

