def calculate_expected_vertices(n, mod1=1000000007, mod2=1000000009):
    """"""

    def pow1(a, b, mod):
        a %= mod
        if b == 0:
            return 1
        res = pow1(a, b // 2, mod)
        res *= res
        res %= mod
        if b % 2:
            res *= a
        return res % mod
    if n == 1:
        return (0, 0)
    if n <= 3:
        return (1, 1)
    p1 = n * (n - 1) % mod1
    p2 = n * (n - 1) % mod2
    q1 = 2 * (n + n - 3) % mod1
    q2 = 2 * (n + n - 3) % mod2
    inv1 = pow1(q1, mod1 - 2, mod1)
    inv2 = pow1(q2, mod2 - 2, mod2)
    ans1 = p1 * inv1 % mod1
    ans2 = p2 * inv2 % mod2
    return (ans1, ans2)
