def min_operations_to_form_2x_plus_2y(N):
    arr = []
    for i in range(1, 33):
        for j in range(i):
            arr.append((1 << i) + (1 << j))
    arr.sort()
    (low, high) = (0, len(arr))
    (res, diff) = (float('inf'), float('inf'))
    while low < high:
        mid = (low + high) // 2
        currdiff = abs(N - arr[mid])
        if currdiff < diff:
            diff = currdiff
            res = mid
        if arr[mid] == N:
            return 0
        if arr[mid] > N:
            high = mid - 1
        else:
            low = mid + 1
    return diff

