        i = 0
        for j in range(N):
            if B[0][j] != 0:
                i = j
                A[i] = -B[0][i]
                break
        for j in range(i + 1, N):
            if abs(A[i] - B[0][j]) == B[i][j]:
                A[j] = B[0][j]
            else:
                A[j] = -B[0][j]
        return A
    results = []
    B = [row[:] for row in initial_B]
    results.append(get_A(B))
    for q in queries:
        update_B(B, q)
        results.append(get_A(B))
    return results

