def count_mismatched_peak_months(T, test_cases):
    results = []
    for case in test_cases:
        (N, M, initial_ratings, rating_changes) = case
        points = [[0 for _ in range(M + 1)] for _ in range(N)]
        for i in range(N):
            points[i][0] = initial_ratings[i]
        mx = [[0, 0] for _ in range(N)]
        arr = [[3000, 0] for _ in range(N)]
        for i in range(N):
            for j in range(1, M + 1):
                points[i][j] = points[i][j - 1] + rating_changes[i][j - 1]
            points[i] = points[i][1:]
            arr[i][1] = points[i][0]
        for i in range(M):
            rank = []
            for j in range(N):
                rank.append(points[j][i])
            rank = sorted(rank, reverse=True)
            for j in range(N):
                pos = rank.index(points[j][i])
                if pos < arr[j][0]:
                    mx[j][1] = i
                    arr[j][0] = pos
            for j in range(N):
                if points[j][i] > arr[j][1]:
                    mx[j][0] = i
                    arr[j][1] = points[j][i]
        ans = 1
        for x in mx:
            if x[0] != x[1]:
                ans += 1
        results.append(ans)
    return results

