def find_longest_file_path_length(input_string: str) -> int:
    depth_length_map = {0: 0}
    max_length = 1
    lines = input_string.split('\n')
    for line in lines:
        name = line.lstrip('\t')
        depth = len(line) - len(name)
        if '.' in name:
            max_length = max(max_length, depth_length_map[depth] + len(name))
        else:
            depth_length_map[depth + 1] = depth_length_map[depth] + len(name) + 1
    return max_length

