from collections import defaultdict

def firing_employees(arr, n):

    def check_prime(val):
        if val <= 1:
            return False
        for i in range(2, int(val ** 0.5) + 1):
            if val % i == 0:
                return False
        return True
    child = defaultdict(list)
    for i in range(n):
        if arr[i] != 0:
            child[arr[i]].append(i + 1)
        else:
            root = i + 1
    cnt = 1
    depth = 1
    curr = child[root]
    next_level = []
    while curr:
        temp = curr.pop()
        next_level.extend(child[temp])
        if check_prime(temp + depth):
            cnt += 1
        if not curr:
            depth += 1
            curr = next_level[:]
            next_level = []
    return cnt

