    P = {(a - 1, b - 1): 1 for (a, b) in debts}
    A = [-1] / n
    A[0] = 0
    for i in range(1, n):
        A[i] = i
        x = i
        while x > 0 and (A[x - 1], A[x]) in P:
            (A[x - 1], A[x]) = (A[x], A[x - 1])
            x -= 1
    for i in range(1, n):
        if (A[i - 1], A[i]) in P:
            return -1
    return [x + 1 for x in A]

