def calculate_minimum_wallpaper_cost(rooms, wallpapers):

    def room_cost(room, wallpapers):
        min_cost = 10 * 18
        perimeter = 2 * (room[0] + room[1])
        if room[2] == 0:
            return 0
        for wallpaper in wallpapers:
            if wallpaper[1] != 0:
                stripes_needed = (perimeter + wallpaper[1] - 1) // wallpaper[1]
                stripes_from_one_roll = wallpaper[0] // room[2]
                if stripes_from_one_roll == 0:
                    continue
                amount_of_rolls = (stripes_needed + stripes_from_one_roll - 1) // stripes_from_one_roll
                min_cost = min([min_cost, amount_of_rolls * wallpaper[2]])
        return min_cost
    return sum([room_cost(room, wallpapers) for room in rooms])

