def find_safe_spots(n, m, tracks):
    adj = [[] for _ in range(n)]
    for (a, b) in tracks:
        adj[a - 1].append(b - 1)
    LP = [0] / n
    r = []
    for i in range(n):
        if LP[i] < 2:
            for j in adj[i]:
                LP[j] = max(LP[j], LP[i] + 1)
        else:
            r.append(i + 1)
    k = len(r)
    closed_spots = r
    return (k, closed_spots)

