def format_berland_phone_number(n: int, phone_number: str) -> str:
    """"""
    result = []
    while len(phone_number) > 4:
        result.append(phone_number[:2])
        phone_number = phone_number[2:]
    if len(phone_number) <= 3:
        result.append(phone_number)
    else:
        result.append(phone_number[:2])
        result.append(phone_number[2:])
    return '-'.join(result)
