def make_all_zeros(test_cases):
    results = []
    for case in test_cases:
        (N, arr) = case
        operations = []

        def operate(x, y, z):
            operations.append((arr[x], arr[y], arr[z]))
            (arr[x], arr[y], arr[z]) = (arr[x] ^ arr[y], arr[y] ^ arr[z], arr[z] ^ arr[x])
        for bit in range(31, -1, -1):
            idx = []
            for i in range(N):
                if arr[i] >> bit & 1:
                    idx.append(i)
                if len(idx) == 3:
                    operate(idx[0], idx[1], idx[2])
                    idx = []
            if len(idx) == 1:
                x = idx[0]
                (y, z, w) = ((x + 1) % N, (x + 2) % N, (x + 3) % N)
                operate(x, y, z)
                operate(y, z, w)
                operate(x, y, z)
            elif len(idx) == 2:
                x = idx[0]
                nxt = []
                for i in range(1, 4):
                    ii = (x + i) % N
                    if ii != idx[1]:
                        nxt.append(ii)
                y = idx[1]
                (z, w) = (nxt[0], nxt[1])
                operate(y, z, w)
                operate(x, y, w)
        results.append((len(operations), operations))
    return results

