def transform_number(a: int, b: int) -> tuple:
    ans = []
    while b == a:
        if b % 10 == 1:
            ans.append(b)
            b //= 10
        elif b % 2 == 0 and b > 0:
            ans.append(b)
            b //= 2
        else:
            break
    if a == b:
        ans.append(a)
        ans.reverse()
        return (True, ans)
    else:
        return (False, [])

