            for j in range(32):
                l_shifted = L >> i << i
                r_shifted = R >> j << j
                if l_shifted >> i & 1 == 0 or r_shifted >> j & 1 == 0:
                    continue
                l_shifted -= 1 << i
                r_shifted -= 1 << j
                if l_shifted & r_shifted:
                    continue
                lr = l_shifted ^ r_shifted
                ma = max(i, j)
                mi = min(i, j)
                mask = (1 << ma) - 1
                p = bin(lr & mask).count('1')
                ip = ma - mi - p
                res += 3 ** mi * 2 ** ip
        return res
    return (solve(r + 1, r + 1) + solve(l, l)) * (solve(l, r + 1) * 2)

